function resizeFeaturedProject(){var i=$(window).height();var c=$(window).width();var g=$("#header").outerHeight(true);var a=$(".featured");var f=i-g;var d=$(".featured").width();var h=$(".featured img");var b=0.55;h.css("position","relative");if(i>c||i<240){var f=d*0.75;a.height(f);h.height(f);h.width(f/b);h.css("left",(d-h.width())/2)}else{a.height(f);if((f/d)>b){h.height(f-b+1);h.width(f/b+1)}else{h.width(d-b+1);h.height(d*b+1)}h.css("left",(d-h.width())/2);h.css("top",(f-h.height())/2)}}function gridToggles(){var b=$("#columns");var c=$("#columns-on");var a=$("#columns-off");b.hide();c.show();a.hide();c.click(function(){b.show();c.hide();a.show()});a.click(function(){b.hide();c.show();a.hide()})}try{Typekit.load()}catch(e){}$(document).ready(function(){$(".project, #info, #copyright, img, .featured div").fadeTo(0,0);resizeFeaturedProject();gridToggles();$("#nav").localScroll({lock:stop,target:"body",duration:350});$(".work div a img").after('<span class="cover"></span>');$(".cover").fadeTo(0,0);$(".work div a").hover(function(){$(this).children(".cover").stop(true,false).fadeTo(0,0.7);$(this).siblings("h4").children("a").addClass("active")},function(){$(this).children(".cover").stop(true,false).fadeTo(500,0);$(this).siblings("h4").children("a").removeClass("active")});$(window).resize(function(){resizeFeaturedProject()})});$(window).load(function(){$("body").css("background-image","none");$(".featured").css("background-image","none");$(".work div img, .featured div, img, .project, #info, #copyright").fadeTo(500,1);$(".work .dev img").fadeTo(250,0.33);$(".featured").click(function(){$("#.featured h2 a").bind("click",function(){window.location.href=this.href;return false});$(".featured h2 a").trigger("click").once()})});